styles.css:
/* Register button logic (show only on pages with mw memberonly message) */
#register-button {
    display: none;
}

:root:has(.memberonly) #member-area-button {
    display: none;
}

:root:has(.memberonly) #register-button {
    display: inline-block;
}

#nav-buttons a {
    text-wrap: nowrap;
}

/* Float mobile nav menu to the right */
@media only screen and (max-width: 600px) {
  #greater-nav-menu {
    flex-direction: row-reverse;
    justify-content: space-between;
    flex-grow: 1;
  }
}

/* Keys instead of asterisks on headers */
.is-style-asterisk:before {
  content: '';
  width: 15px;
  height: 48px;
  background: var(--wp--preset--color--accent, currentColor);
  clip-path: path('M7.5 0a3.493 3.493 0 0 0-3.492 3.492 3.493 3.493 0 0 0 2.03 3.168v1.902H0v16.39h6v1.65h.421v3.986H8.58v-3.986H9v-1.65h6V8.562H8.963V6.66a3.493 3.493 0 0 0 2.03-3.168A3.493 3.493 0 0 0 7.5 0zm0 1.309a2.183 2.183 0 0 1 2.183 2.183 2.183 2.183 0 0 1-.72 1.612v-.056h-.384v-.66H6.42v.66h-.383v.056a2.183 2.183 0 0 1-.721-1.612A2.183 2.183 0 0 1 7.5 1.31z');
  display: block;
}

/* Hover color for base background links */
.has-base-background-color.wp-block-button__link:hover {
background-color: transparent !important;
    color: var(--wp--preset--color--base) !important;
}

.has-base-background-color.wp-block-button__link {
    border: 1px solid var(--wp--preset--color--base);
}
